INSERT INTO "PUBLIC"."HF_FORMULA" VALUES
('6349c44a-07d1-11eb-a5e8-ef9de1a822a0', 'F001', 'percent of target', 'GROOVY', 'C', 'ans', STRINGDECODE('import org.apache.commons.lang3.math.*;\nimport java.lang.*;\n\nif ($P{actual} > 0 && $P{target} > 0 ) {\n    ans = $P{actual} \u00f7 $P{target} \u00d7 100;\n    return ans;\n}\nif (\n    ($P{actual} <= 0 && $P{target} < 0 && $P{actual} >= $P{target})\n    ||\n    ($P{actual} <= 0 && $P{target} < 0 && $P{actual} < $P{target})\n    ||\n    ($P{actual} > 0 && $P{target} < 0 )\n) {\n    ans = (((($P{target} - $P{actual}) \u00f7 $P{target}) \u00d7 100) + 100);\n    return ans;\n}\nif ($P{actual} < 0 && $P{target} > 0 ) {\n    ans = (((($P{actual} - $P{target}) \u00f7 $P{target}) \u00d7 100) + 100);\n    return ans;\n}\nif ($P{actual} == 0 && $P{target} > 0 ) {\n    ans = BigDecimal.ZERO;\n    return ans;\n}\nif ($P{actual} >= 0 && $P{target} == 0 ) {\n    ans = (($P{actual} \u00d7 100) + 100);\n    return ans;\n}\nif ($P{actual} < 0 && $P{target} == 0 ) {\n    ans = (($P{actual} \u00d7 100) + 100);\n    return ans;\n}\nans = BigDecimal.ZERO;\nreturn ans;'), '', 'admin', TIMESTAMP '2020-10-06 12:42:26', 'admin', TIMESTAMP '2021-02-24 13:39:23.839');      
